package ifElseStatements;

public class if_else_statement_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a= 10;
		int b= 20;
		
		if (a>b)
		{
			System.out.println("a is greater than b");
		} 
		
		else
		{
			System.out.println("b is greater than a");
		}

	}

}
