package loops;

public class Nested_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int r=0; r<=4; r++)
		{
			for(int c=0;c<=2;c++)
				
			{
				System.out.print(r+  "  , " +c + "  || " );
			}
			{
				System.out.println();
			}
			
		}
		
		
		

	}

}
